from django.shortcuts import render
from django.views import View # <- View class to handle requests
from django.http import HttpResponse # <- a class to handle sending a type of response
from django.views.generic.base import TemplateView


# Create your views here.

# Here we will be creating a class called Home and extending it from the View class
class Home(TemplateView):
    template_name = "home.html"
    

class About(TemplateView):
    template_name = "about.html"

class BreweryList(TemplateView):
    template_name = "brewery_list.html"

    def get_context_data(self, **kwargs):
        context = super().get_context_data(**kwargs)
        context["breweries"] = breweries # this is where we add the key into our context object for the view to use
        return context

class BeerList(TemplateView):
    template_name = "beer_list.html"

    def get_context_data(self, **kwargs):
        context = super().get_context_data(**kwargs)
        context["beers"] = beers # this is where we add the key into our context object for the view to use
        return context

#adds brewery class for mock database data
class Brewery:
    def __init__(self, name, image, location, bio):
        self.name = name
        self.image = image
        self.location = location
        self.bio = bio

class Beer:
    def __init__(self, title, type, abv):
        self.title = title
        self.type = type
        self.abv = abv

breweries = [
    Brewery('Hudson Valley Brewery','data:image/png;base64,','Beacon, New York','At Hudson Valley Brewery, we like to think of our beer as the product of a wide variety of influences.'),
    Brewery('Grimm Artisanal Ales','data:image/png;base64,','Brooklyn, NY','Brooklyn’s Grimm Artisanal Ales is renowned worldwide both as a stylistic innovator and a respectful champion of tradition. In addition to winning awards and accolades within the brewing industry (including New York State Brewery of the Year in 2021 and 2022), Grimm’s focus on elegant, complex ales has built a devoted following of aficionados who appreciate the brewery’s deft hand with spontaneous fermentation just as much as their aromatic hazy IPAs. Part small business, part art project, this auteur brewery reflects the creative vision of the Grimm family and their jolly band of collaborators.')
]

beers =[
    Beer('AMBIO','Sour DIPA w/ wheat & oat, milk sugar, raspberry, lychee, toasted coconut & rose petals','8%'),
    Beer('AMBIENT FIZZ','Spritzy Spontaneous Sour','4%')
]